        private const char Up = '(';

        public int SolvePart1()
        {
            return Directions.Aggregate(0, MoveFloor);
        }

        public int SolvePart2()
        {
            var floor = 0;

            for (var i = 0; i < Directions.Length; ++i)
            {
                floor = MoveFloor(floor, Directions[i]);

                if (floor < 0)
                {
                    return i + 1;
                }
            }

            throw new Exception("Input never leads to a basement floor");
        }

        private static int MoveFloor(int currentFloor, char direction)
        {
            if (direction == Up)
            {
                return currentFloor + 1;
            }

            return currentFloor - 1;
        }
